async function fetchData(url,options={}){try{const response=await fetch(url,{headers:{"Content-Type":"application/json",...options.headers},...options});return await response.json()}catch(error){console.error("Fetch error:",error),showAlert("Une erreur est survenue","danger")}}function showAlert(message,type="success"){const alert=document.createElement("div");alert.className=`alert alert-${type} alert-dismissible fade show`,alert.role="alert",alert.innerHTML=`\n        ${message}\n        <button type="button" class="btn-close" data-bs-dismiss="alert"></button>\n    `,document.body.prepend(alert)}document.addEventListener("DOMContentLoaded",function(){[].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]')).map(function(tooltipTriggerEl){return new bootstrap.Tooltip(tooltipTriggerEl)});document.querySelectorAll(".alert").forEach(alert=>{setTimeout(()=>{alert.classList.add("fade-out"),setTimeout(()=>alert.remove(),500)},5e3)})});